# Stack Implementation
class Stack:
    def __init__(self):
        self.items = []

    def push(self, item):
        self.items.append(item)
        print(f"Pushed {item}: {self.items}")

    def pop(self):
        if not self.is_empty():
            item = self.items.pop()
            print(f"Popped {item}: {self.items}")
            return item
        return None

    def peek(self):
        return self.items[-1] if not self.is_empty() else None

    def is_empty(self):
        return len(self.items) == 0

# Example: Valid Parentheses
def is_valid_parentheses(s):
    stack = []
    mapping = {')': '(', '}': '{', ']': '['}

    for char in s:
        if char in mapping:
            top = stack.pop() if stack else '#'
            if mapping[char] != top:
                return False
        else:
            stack.append(char)

    return not stack

print("Stack Demo:")
s = Stack()
s.push(10)
s.push(20)
s.push(30)
s.pop()

print("\nValid Parentheses Check:")
print(f"'()[]{{}}' is valid: {is_valid_parentheses('()[]{}')}")
print(f"'(]' is valid: {is_valid_parentheses('(]')}")

class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def count_nodes(root):
    """Count total nodes in binary tree"""
    if root is None:
        return 0
    return 1 + count_nodes(root.left) + count_nodes(root.right)

def max_depth(root):
    """Find height of binary tree"""
    if root is None:
        return 0
    return 1 + max(max_depth(root.left), max_depth(root.right))

