class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def search_bst(root, target):
    """Search for target value in BST"""
    if not root or root.val == target:
        return root
    if target < root.val:
        return search_bst(root.left, target)
    return search_bst(root.right, target)

def insert_bst(root, val):
    """Insert new value into BST"""
    if not root:
        return TreeNode(val)
    if val < root.val:
        root.left = insert_bst(root.left, val)
    elif val > root.val:
        root.right = insert_bst(root.right, val)
    return root

def delete_bst(root, key):
    """Delete node with given key from BST"""
    if not root:
        return None

    if key < root.val:
        root.left = delete_bst(root.left, key)
    elif key > root.val:
        root.right = delete_bst(root.right, key)
    else:
        # Node with one child or no child
        if not root.left:
            return root.right
        if not root.right:
            return root.left

        # Node with two children: get inorder successor
        min_node = root.right
        while min_node.left:
            min_node = min_node.left
        root.val = min_node.val
        root.right = delete_bst(root.right, min_node.val)

    return root

def validate_bst(root, min_val=float('-inf'), max_val=float('inf')):
    """Validate if tree is valid BST"""
    if not root:
        return True
    if root.val <= min_val or root.val >= max_val:
        return False
    return (validate_bst(root.left, min_val, root.val) and
            validate_bst(root.right, root.val, max_val))

from collections import deque

class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def level_order_traversal(root):
    """Standard BFS - returns nodes grouped by level"""
    if not root:
        return []

    result = []
    queue = deque([root])

    while queue:
        level_size = len(queue)  # Nodes at current level
        current_level = []

        for _ in range(level_size):
            node = queue.popleft()
            current_level.append(node.val)

            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)

        result.append(current_level)

    return result

def zigzag_traversal(root):
    """Zigzag BFS - alternate left-to-right direction"""
    if not root:
        return []

    result = []
    queue = deque([root])
    left_to_right = True

    while queue:
        level_size = len(queue)
        current_level = deque()

        for _ in range(level_size):
            node = queue.popleft()

            # Add to level based on direction
            if left_to_right:
                current_level.append(node.val)
            else:
                current_level.appendleft(node.val)

            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)

        result.append(list(current_level))
        left_to_right = not left_to_right

    return result

def right_side_view(root):
    """Get rightmost node at each level"""
    if not root:
        return []

    result = []
    queue = deque([root])

    while queue:
        level_size = len(queue)

        for i in range(level_size):
            node = queue.popleft()

            # Last node in level is rightmost
            if i == level_size - 1:
                result.append(node.val)

            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)

    return result

from collections import defaultdict

class Graph:
    def __init__(self):
        self.graph = defaultdict(list)

    def add_edge(self, u, v, directed=False):
        self.graph[u].append(v)
        if not directed:
            self.graph[v].append(u)

    def dfs(self, start, visited=None):
        """Basic DFS traversal"""
        if visited is None:
            visited = set()

        visited.add(start)
        print(start, end=' ')

        for neighbor in self.graph[start]:
            if neighbor not in visited:
                self.dfs(neighbor, visited)

        return visited

    def has_cycle_undirected(self, node, visited, parent):
        """Detect cycle in undirected graph"""
        visited.add(node)

        for neighbor in self.graph[node]:
            if neighbor not in visited:
                if self.has_cycle_undirected(neighbor, visited, node):
                    return True
            elif neighbor != parent:
                # Visited node that's not parent = cycle!
                return True

        return False

    def has_cycle_directed(self, node, visited, rec_stack):
        """Detect cycle in directed graph"""
        visited.add(node)
        rec_stack.add(node)

        for neighbor in self.graph[node]:
            if neighbor not in visited:
                if self.has_cycle_directed(neighbor, visited, rec_stack):
                    return True
            elif neighbor in rec_stack:
                # Back edge to node in recursion stack = cycle!
                return True

        rec_stack.remove(node)
        return False

    def count_connected_components(self, num_nodes):
        """Count connected components (undirected graph)"""
        visited = set()
        count = 0

        for node in range(num_nodes):
            if node not in visited:
                self.dfs(node, visited)
                count += 1

        return count

    def find_path(self, start, end, visited=None, path=None):
        """Find a path between two nodes"""
        if visited is None:
            visited = set()
        if path is None:
            path = []

        visited.add(start)
        path.append(start)

        if start == end:
            return path

        for neighbor in self.graph[start]:
            if neighbor not in visited:
                result = self.find_path(neighbor, end, visited, path)
                if result:
                    return result

        path.pop()
        return None

from collections import deque, defaultdict

class Graph:
    def __init__(self):
        self.graph = defaultdict(list)

    def add_edge(self, u, v, directed=False):
        self.graph[u].append(v)
        if not directed:
            self.graph[v].append(u)

    def bfs(self, start):
        """Basic BFS traversal"""
        visited = set()
        queue = deque([start])
        visited.add(start)
        result = []

        while queue:
            node = queue.popleft()
            result.append(node)

            for neighbor in self.graph[node]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append(neighbor)

        return result

    def shortest_path(self, start, end):
        """Find shortest path using BFS"""
        if start == end:
            return [start]

        visited = set([start])
        queue = deque([start])
        parent = {start: None}

        while queue:
            node = queue.popleft()

            if node == end:
                # Reconstruct path
                path = []
                current = end
                while current is not None:
                    path.append(current)
                    current = parent[current]
                return path[::-1]

            for neighbor in self.graph[node]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    parent[neighbor] = node
                    queue.append(neighbor)

        return None  # No path found

    def shortest_distances(self, start, num_nodes):
        """Find shortest distance from start to all nodes"""
        distance = [-1] * num_nodes
        distance[start] = 0
        queue = deque([start])

        while queue:
            node = queue.popleft()

            for neighbor in self.graph[node]:
                if distance[neighbor] == -1:
                    distance[neighbor] = distance[node] + 1
                    queue.append(neighbor)

        return distance

    def is_bipartite(self, num_nodes):
        """Check if graph is bipartite using 2-coloring"""
        color = [-1] * num_nodes

        # Check all components
        for start in range(num_nodes):
            if color[start] == -1:
                queue = deque([start])
                color[start] = 0

                while queue:
                    node = queue.popleft()

                    for neighbor in self.graph[node]:
                        if color[neighbor] == -1:
                            # Color with opposite color
                            color[neighbor] = 1 - color[node]
                            queue.append(neighbor)
                        elif color[neighbor] == color[node]:
                            # Same color = not bipartite
                            return False

        return True

from collections import defaultdict, deque

class Graph:
    def __init__(self, vertices):
        self.V = vertices
        self.graph = defaultdict(list)

    def add_edge(self, u, v):
        self.graph[u].append(v)

    def topological_sort_dfs(self):
        """DFS-based topological sort"""
        visited = set()
        rec_stack = set()
        result = []

        def dfs(node):
            if node in rec_stack:
                return False  # Cycle detected
            if node in visited:
                return True

            visited.add(node)
            rec_stack.add(node)

            for neighbor in self.graph[node]:
                if not dfs(neighbor):
                    return False

            rec_stack.remove(node)
            result.append(node)  # Add after visiting all descendants
            return True

        for node in range(self.V):
            if node not in visited:
                if not dfs(node):
                    return None  # Cycle exists

        return result[::-1]  # Reverse to get correct order

    def topological_sort_kahn(self):
        """Kahn's algorithm (BFS-based)"""
        # Calculate in-degrees
        in_degree = [0] * self.V
        for node in range(self.V):
            for neighbor in self.graph[node]:
                in_degree[neighbor] += 1

        # Initialize queue with zero in-degree nodes
        queue = deque([i for i in range(self.V) if in_degree[i] == 0])
        result = []

        while queue:
            node = queue.popleft()
            result.append(node)

            # Reduce in-degree for neighbors
            for neighbor in self.graph[node]:
                in_degree[neighbor] -= 1
                if in_degree[neighbor] == 0:
                    queue.append(neighbor)

        # Check if all vertices processed (no cycle)
        if len(result) != self.V:
            return None  # Cycle exists

        return result

# Example: Course prerequisites
g = Graph(4)
g.add_edge(0, 1)  # Course 0 before 1
g.add_edge(0, 2)  # Course 0 before 2
g.add_edge(1, 3)  # Course 1 before 3
g.add_edge(2, 3)  # Course 2 before 3

print("DFS:", g.topological_sort_dfs())   # [0, 1, 2, 3] or [0, 2, 1, 3]
print("Kahn's:", g.topological_sort_kahn()) # [0, 1, 2, 3] or [0, 2, 1, 3]

from functools import lru_cache

# Top-down: Fibonacci with memoization
@lru_cache(maxsize=None)
def fib_memo(n: int) -> int:
    if n <= 1:
        return n
    return fib_memo(n - 1) + fib_memo(n - 2)

# Bottom-up: Fibonacci with tabulation
def fib_tab(n: int) -> int:
    if n <= 1:
        return n
    dp = [0] * (n + 1)
    dp[0], dp[1] = 0, 1
    for i in range(2, n + 1):
        dp[i] = dp[i - 1] + dp[i - 2]
    return dp[n]

print(fib_memo(10))  # 55
print(fib_tab(10))   # 55

# Climbing Stairs - Tabulation
def climbStairs(n: int) -> int:
    if n <= 2:
        return n
    dp = [0] * (n + 1)
    dp[1], dp[2] = 1, 2
    for i in range(3, n + 1):
        dp[i] = dp[i - 1] + dp[i - 2]
    return dp[n]

# House Robber - Tabulation
def rob(nums: list[int]) -> int:
    if not nums:
        return 0
    if len(nums) == 1:
        return nums[0]
    dp = [0] * len(nums)
    dp[0] = nums[0]
    dp[1] = max(nums[0], nums[1])
    for i in range(2, len(nums)):
        dp[i] = max(dp[i - 1], nums[i] + dp[i - 2])
    return dp[-1]

# Space-Optimized House Robber - O(1) space
def rob_optimized(nums: list[int]) -> int:
    if not nums:
        return 0
    prev2, prev1 = 0, 0
    for num in nums:
        prev2, prev1 = prev1, max(prev1, num + prev2)
    return prev1

print(climbStairs(5))  # 8
print(rob([2,7,9,3,1]))  # 12
print(rob_optimized([2,7,9,3,1]))  # 12

# Longest Common Subsequence
def longestCommonSubsequence(text1: str, text2: str) -> int:
    m, n = len(text1), len(text2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if text1[i - 1] == text2[j - 1]:
                dp[i][j] = 1 + dp[i - 1][j - 1]
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    return dp[m][n]

# Edit Distance
def minDistance(word1: str, word2: str) -> int:
    m, n = len(word1), len(word2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    # Base cases
    for i in range(m + 1):
        dp[i][0] = i
    for j in range(n + 1):
        dp[0][j] = j

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if word1[i - 1] == word2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1]
            else:
                dp[i][j] = 1 + min(
                    dp[i - 1][j],      # delete
                    dp[i][j - 1],      # insert
                    dp[i - 1][j - 1]   # replace
                )

    return dp[m][n]

# Examples
print(longestCommonSubsequence('abcde', 'ace'))  # 3
print(minDistance('horse', 'ros'))  # 3

# Binary Search on Answer: Koko Eating Bananas
def minEatingSpeed(piles: list[int], h: int) -> int:
    def canFinish(speed: int) -> bool:
        hours = sum((pile + speed - 1) // speed for pile in piles)
        return hours <= h

    left, right = 1, max(piles)
    while left < right:
        mid = left + (right - left) // 2
        if canFinish(mid):
            right = mid  # Try slower speed
        else:
            left = mid + 1  # Need faster speed
    return left

# Search in Rotated Sorted Array
def search_rotated(nums: list[int], target: int) -> int:
    left, right = 0, len(nums) - 1

    while left <= right:
        mid = left + (right - left) // 2
        if nums[mid] == target:
            return mid

        # Check which half is sorted
        if nums[left] <= nums[mid]:  # Left half sorted
            if nums[left] <= target < nums[mid]:
                right = mid - 1
            else:
                left = mid + 1
        else:  # Right half sorted
            if nums[mid] < target <= nums[right]:
                left = mid + 1
            else:
                right = mid - 1
    return -1

print(minEatingSpeed([3,6,7,11], 8))  # 4
print(search_rotated([4,5,6,7,0,1,2], 0))  # 4

# Activity Selection (Maximum non-overlapping intervals)
def max_meetings(start: list[int], end: list[int]) -> int:
    # Sort by end time
    meetings = sorted(zip(start, end), key=lambda x: x[1])
    count = 0
    last_end = 0

    for s, e in meetings:
        if s >= last_end:
            count += 1
            last_end = e

    return count

# Jump Game
def canJump(nums: list[int]) -> bool:
    farthest = 0
    for i in range(len(nums)):
        if i > farthest:
            return False
        farthest = max(farthest, i + nums[i])
        if farthest >= len(nums) - 1:
            return True
    return True

# Gas Station
def canCompleteCircuit(gas: list[int], cost: list[int]) -> int:
    if sum(gas) < sum(cost):
        return -1

    start = 0
    tank = 0
    for i in range(len(gas)):
        tank += gas[i] - cost[i]
        if tank < 0:
            start = i + 1
            tank = 0

    return start

print(max_meetings([1,3,0,5,8,5], [2,4,6,7,9,9]))  # 4
print(canJump([2,3,1,1,4]))  # True
print(canCompleteCircuit([1,2,3,4,5], [3,4,5,1,2]))  # 3

# Trie (Prefix Tree) Implementation

class TrieNode:
    def __init__(self):
        self.children = {}  # character -> TrieNode
        self.is_end_of_word = False

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word: str) -> None:
        """Insert a word into the trie. O(m) time."""
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end_of_word = True

    def search(self, word: str) -> bool:
        """Search if word exists in trie. O(m) time."""
        node = self.root
        for char in word:
            if char not in node.children:
                return False
            node = node.children[char]
        return node.is_end_of_word

    def startsWith(self, prefix: str) -> bool:
        """Check if any word starts with prefix. O(m) time."""
        node = self.root
        for char in prefix:
            if char not in node.children:
                return False
            node = node.children[char]
        return True

    def findWordsWithPrefix(self, prefix: str) -> list:
        """Find all words starting with given prefix."""
        node = self.root
        # Navigate to prefix end
        for char in prefix:
            if char not in node.children:
                return []
            node = node.children[char]

        # DFS to collect all words from this point
        result = []
        self._dfs(node, prefix, result)
        return result

    def _dfs(self, node, path, result):
        if node.is_end_of_word:
            result.append(path)
        for char, child in node.children.items():
            self._dfs(child, path + char, result)

# Example Usage
trie = Trie()
words = ["apple", "app", "application", "apply", "banana", "band"]

print("Inserting words:", words)
for word in words:
    trie.insert(word)

print("\nSearch Results:")
print(f"Search 'apple': {trie.search('apple')}")  # True
print(f"Search 'app': {trie.search('app')}")      # True
print(f"Search 'appl': {trie.search('appl')}")    # False (prefix only)

print("\nPrefix Search:")
print(f"StartsWith 'app': {trie.startsWith('app')}")  # True
print(f"StartsWith 'ban': {trie.startsWith('ban')}")  # True
print(f"StartsWith 'cat': {trie.startsWith('cat')}")  # False

print("\nAutocomplete (words with prefix 'app'):")
print(trie.findWordsWithPrefix('app'))  # ['apple', 'app', 'application', 'apply']
